import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../../context/CartContext';
import { useAuth } from '../../context/AuthContext';
import { PlusIcon, MinusIcon, ShoppingCartIcon } from '@heroicons/react/24/outline';
import { toast } from 'react-toastify';

const ProductCard = ({ product }) => {
  const { addToCart, updateCartItem, getItemQuantity } = useCart();
  const { isManager } = useAuth();
  const [isLoading, setIsLoading] = useState(false);

  const currentQuantity = getItemQuantity(product.id);

  const handleAddToCart = async () => {
    setIsLoading(true);
    try {
      await addToCart(product, 1);
    } catch (error) {
      toast.error('Failed to add product to cart');
    } finally {
      setIsLoading(false);
    }
  };

  const handleUpdateQuantity = async (newQuantity) => {
    if (newQuantity < 0) return;

    setIsLoading(true);
    try {
      await updateCartItem(product.id, newQuantity);
    } catch (error) {
      toast.error('Failed to update cart');
    } finally {
      setIsLoading(false);
    }
  };

  const isOutOfStock = product.stockQuantity === 0;

  return (
    <div className="card-hover p-4 transition-all duration-200">
      {/* Product Image */}
      <Link to={`/products/${product.id}`} className="block">
        <div className="w-full h-48 bg-gradient-to-br from-primary-100 to-primary-200 rounded-lg mb-4 flex items-center justify-center overflow-hidden">
          {product.imageUrl ? (
            <img
              src={product.imageUrl}
              alt={product.name}
              className="w-full h-full object-cover hover:scale-105 transition-transform duration-200"
            />
          ) : (
            <div className="text-primary-600 text-4xl font-bold">
              {product.name.charAt(0).toUpperCase()}
            </div>
          )}
        </div>
      </Link>

      {/* Product Info */}
      <div className="space-y-2">
        <Link to={`/products/${product.id}`}>
          <h3 className="font-semibold text-gray-900 text-lg line-clamp-2 hover:text-primary-600 transition-colors duration-200">
            {product.name}
          </h3>
        </Link>

        <p className="text-sm text-gray-600 bg-gray-100 px-2 py-1 rounded-full inline-block">
          {product.category}
        </p>

        <div className="flex items-center justify-between">
          <div>
            <p className="text-2xl font-bold text-primary-600">
              ${parseFloat(product.price).toFixed(2)}
            </p>
            <p className="text-sm text-gray-500">
              Stock: {product.stockQuantity}
            </p>
          </div>

          {isOutOfStock && (
            <span className="text-error-600 font-medium text-sm bg-error-100 px-2 py-1 rounded">
              Out of Stock
            </span>
          )}
        </div>

        {/* Add to Cart Controls */}
        {!isOutOfStock && (
          <div className="pt-2">
            {currentQuantity === 0 ? (
              <button
                onClick={handleAddToCart}
                disabled={isLoading}
                className="w-full btn-primary flex items-center justify-center space-x-2"
              >
                <ShoppingCartIcon className="w-4 h-4" />
                <span>{isLoading ? 'Adding...' : 'Add to Cart'}</span>
              </button>
            ) : (
              <div className="flex items-center justify-between">
                <button
                  onClick={() => handleUpdateQuantity(currentQuantity - 1)}
                  disabled={isLoading}
                  className="w-8 h-8 rounded-full bg-primary-100 text-primary-600 hover:bg-primary-200 flex items-center justify-center transition-colors"
                >
                  <MinusIcon className="w-4 h-4" />
                </button>

                <span className="font-semibold text-lg px-4">
                  {currentQuantity}
                </span>

                <button
                  onClick={() => handleUpdateQuantity(currentQuantity + 1)}
                  disabled={isLoading || currentQuantity >= product.stockQuantity}
                  className="w-8 h-8 rounded-full bg-primary-100 text-primary-600 hover:bg-primary-200 flex items-center justify-center transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  <PlusIcon className="w-4 h-4" />
                </button>
              </div>
            )}
          </div>
        )}

        {/* Admin/Manager Actions */}
        {isManager() && (
          <div className="pt-2 border-t border-gray-200">
            <div className="flex space-x-2">
              <button className="text-xs bg-secondary-100 text-secondary-700 px-2 py-1 rounded hover:bg-secondary-200 transition-colors">
                Edit
              </button>
              <button className="text-xs bg-error-100 text-error-700 px-2 py-1 rounded hover:bg-error-200 transition-colors">
                Delete
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductCard;
